#include <iostream>
#include <string>

using namespace std;

string NUM =
"";

int main()
{
    int64_t numArray[NUM.length()];
    
    for (int i = 0; i < NUM.length(); i++) {
        
        char c = NUM.at(i);
        
        int ch = c - '0';
        
        numArray[i] = ch;
        
    }
    
    int64_t max = 0;
    
    for (int j = 0; j < NUM.length(); j++) {
        
        int64_t temp =
        numArray[j] * numArray[j+1] * numArray[j+2] * numArray[j+3] * numArray[j+4] *
        numArray[j+5] * numArray[j+6] * numArray[j+7] * numArray[j+8] * numArray[j+9] * numArray[j+10] *
        numArray[j+11] * numArray[j+12];
        ;
        
        if (temp > max)
            max = temp;
        
    }
    
    cout << max << endl;
    
}
